+.card-info {
+  font-size: 0.9rem;
+  margin-bottom: 10px;
+  color: #495057;
+}
+
+.card-info p {
+  margin: 4px 0;
+}
+
+.card-button {
+  padding: 8px 12px;
+  background-color: var(--primary-color);
+  color: #fff;
+  border: none;
+  border-radius: 4px;
+  cursor: pointer;
+  transition: background-color var(--transition-speed) ease, transform var(--transition-speed) ease;
+}
+
+.card-button:hover {
+  background-color: var(--hover-link-color);
+  transform: scale(1.02);
+}
+
+.card-details {
+  margin-top: 10px;
+  font-size: 0.9rem;
+  color: var(--secondary-color);
+  line-height: 1.4;
+  animation: fadeIn 0.3s ease-in-out;
+}
+
+.load-more {
+  padding: 10px 20px;
+  background-color: var(--primary-color);
+  color: #fff;
+  border: none;
+  border-radius: 4px;
+  cursor: pointer;
+  font-size: 1rem;
+  transition: background-color var(--transition-speed) ease, transform var(--transition-speed) ease;
+  display: block;
+  margin: 20px auto;
+}
+
+.load-more:hover {
+  background-color: var(--hover-link-color);
+  transform: scale(1.05);
+}
+
+.filter-popup {
+  position: fixed;
+  top: 20px;
+  left: 50%;
+  transform: translateX(-50%);
+  background-color: var(--primary-color);
+  color: #fff;
+  padding: 10px 20px;
+  border-radius: 8px;
+  box-shadow: 0 2px 5px rgba(0,0,0,0.2);
+  animation: slideDown 0.5s ease, fadeOut 0.5s ease 2.5s forwards;
+  z-index: 1000;
+}
+
+@keyframes slideDown {
+  from {
+    opacity: 0;
+    transform: translateX(-50%) translateY(-20px);
+  }
+  to {
+    opacity: 1;
+    transform: translateX(-50%) translateY(0);
+  }
+}
+
+@keyframes fadeOut {
+  to {
+    opacity: 0;
+  }
+}
+
+@keyframes fadeIn {
+  from { opacity: 0; transform: translateY(10px); }
+  to { opacity: 1; transform: translateY(0); }
+}
+
+/* No results message */
+.no-results {
+  font-size: 1rem;
+  text-align: center;
+  color: var(--secondary-color);
+  margin-top: 50px;
+}
+
+@media (max-width: 768px) {
+  .app-container {
+    flex-direction: column;
+  }
+  
+  .sidebar {
+    width: 100%;
+    border-right: none;
+    border-bottom: 1px solid var(--border-color);
+  }
+  
+  .content {
+    padding: 15px;
+  }
+}

