commits.content:
/* Global Styles & Variables */
:root {
  --primary-color: #007bff;
  --secondary-color: #6c757d;
  --background-color: #e9ecef;
  --card-bg: #ffffff;
  --border-color: #ced4da;
  --font-family: 'Roboto', sans-serif;
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

body {
  font-family: var(--font-family);
  background-color: var(--background-color);
  color: #343a40;
}

/* Layout */
.app-container {
  display: flex;
  height: 100vh;
}

/* Sidebar / Filter Bar */
.sidebar {
  width: 250px;
  background-color: #f8f9fa;
  border-right: 1px solid var(--border-color);
  padding: 20px;
  overflow-y: auto;
}

.filter-bar {
  display: flex;
  flex-direction: column;
}

.filter-heading {
  margin-bottom: 20px;
  font-size: 1.25rem;
  color: #212529;
}

.filter-item {
  margin-bottom: 15px;
  display: flex;
  flex-direction: column;
}

.filter-item label {
  margin-bottom: 5px;
  font-weight: 500;
}

.filter-input {
  padding: 8px;
  border-radius: 4px;
  border: 1px solid var(--border-color);
  font-size: 0.9rem;
  transition: border 0.3s ease;
}

.filter-input:focus {
  outline: none;
  border-color: var(--primary-color);
}

/* Main Content */
.content {
  flex: 1;
  padding: 20px;
  overflow-y: auto;
}

/* Card Styles */
.card {
  background-color: var(--card-bg);
  border: 1px solid var(--border-color);
  border-radius: 8px;
  padding: 15px;
  margin-bottom: 15px;
  box-shadow: 0 2px 5px rgba(0,0,0,0.1);
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.card:hover {
  transform: translateY(-3px);
  box-shadow: 0 4px 10px rgba(0,0,0,0.15);
}

.card-header {
  margin-bottom: 10px;
}

.card-name {
  font-size: 1.2rem;
  margin-bottom: 5px;
  color: #212529;
}

.card-headline {
  font-size: 0.9rem;
  color: var(--secondary-color);
}

.card-info {
  font-size: 0.9rem;
  margin-bottom: 10px;
  color: #495057;
}

.card-info p {
  margin: 4px 0;
}

.card-button {
  padding: 8px 12px;
  background-color: var(--primary-color);
  color: #fff;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.card-button:hover {
  background-color: darken(var(--primary-color), 10%);
}

.card-details {
  margin-top: 10px;
  font-size: 0.9rem;
  color: #343a40;
  line-height: 1.4;
  animation: fadeIn 0.3s ease-in-out;
}

/* Fade-in Animation */
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

/* No results message */
.no-results {
  font-size: 1rem;
  text-align: center;
  color: var(--secondary-color);
  margin-top: 50px;
}

/* Responsive */
@media (max-width: 768px) {
  .app-container {
    flex-direction: column;
  }

  .sidebar {
    width: 100%;
    border-right: none;
    border-bottom: 1px solid var(--border-color);
  }

  .content {
    padding: 15px;
  }
}
