// src/components/FilterBar.js
import React from 'react';
import { Box, TextField, Button, Typography } from '@mui/material';
import Autocomplete from '@mui/material/Autocomplete';

const collegeOptions = [
  "Indian Institute of Technology",
  "Indian Institute of Management",
  "Stanford University",
  "Harvard University",
  "MIT",
  "University of Cambridge",
  "University of Oxford"
];

const industryOptions = [
  "Healthcare",
  "Events & Networking",
  "Construction/Robotics",
  "Finance",
  "EdTech",
  "SaaS"
];

function FilterBar({ filters, setFilters, onApply, onClear }) {
  return (
    <Box
      sx={{
        width: 300,
        padding: '1rem',
        borderRight: '1px solid #ddd',
        display: 'flex',
        flexDirection: 'column',
        gap: 2,
        background: '#f9f9f9',
        height: '100vh',
        position: 'sticky',
        top: 0,
      }}
    >
      <Typography variant="h6" color="primary">
        Filters
      </Typography>

      <Autocomplete
        value={filters.college}
        onChange={(event, newValue) =>
          setFilters((prev) => ({ ...prev, college: newValue }))
        }
        options={collegeOptions}
        renderInput={(params) => (
          <TextField {...params} label="College" variant="outlined" />
        )}
      />

      <Autocomplete
        value={filters.industry}
        onChange={(event, newValue) =>
          setFilters((prev) => ({ ...prev, industry: newValue }))
        }
        options={industryOptions}
        renderInput={(params) => (
          <TextField {...params} label="Industry" variant="outlined" />
        )}
      />

      <TextField
        value={filters.minExperience}
        onChange={(e) =>
          setFilters((prev) => ({
            ...prev,
            minExperience: e.target.value,
          }))
        }
        label="Min. Experience (years)"
        variant="outlined"
        type="number"
      />

      <Button variant="contained" onClick={onApply}>
        Apply Filters
      </Button>
      <Button variant="outlined" onClick={onClear}>
        Clear Filters
      </Button>
    </Box>
  );
}

export default FilterBar;
