/* StartupCard.css */
.detail-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  padding: 20px;
  backdrop-filter: blur(3px);
}

.detail-modal {
  background-color: white;
  border-radius: 12px;
  width: 100%;
  max-width: 700px;
  max-height: 90vh;
  overflow-y: auto;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  position: relative;
  padding: 0;
  animation: fadeIn 0.5s ease-in-out;
}

.close-button {
  position: absolute;
  top: 15px;
  right: 15px;
  background: rgba(0, 0, 0, 0.1);
  border: none;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  color: #333;
  transition: all 0.2s ease;
  z-index: 10;
}

.close-button:hover {
  background: rgba(0, 0, 0, 0.2);
  transform: scale(1.1);
}

/* (Keep the rest of your CSS for modal-header, profile-info, modal-content, etc.) */

  .close-button {
    position: absolute;
    top: 15px;
    right: 15px;
    background: rgba(0, 0, 0, 0.1);
    border: none;
    width: 32px;
    height: 32px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    color: #333;
    transition: all 0.2s ease;
    z-index: 10;
  }
  
  .close-button:hover {
    background: rgba(0, 0, 0, 0.2);
    transform: scale(1.1);
  }
  
  .modal-header {
    background: linear-gradient(135deg, var(--primary), var(--secondary));
    color: white;
    padding: 30px;
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
    position: relative;
  }
  
  .profile-info {
    display: flex;
    align-items: center;
    gap: 20px;
  }
  
  .profile-avatar {
    width: 80px;
    height: 80px;
    background-color: rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 28px;
    font-weight: bold;
    color: white;
    border: 3px solid rgba(255, 255, 255, 0.5);
  }
  
  .modal-header h2 {
    margin: 0 0 5px 0;
    font-size: 24px;
  }
  
  .headline {
    margin: 0 0 10px 0;
    opacity: 0.9;
    font-size: 16px;
  }
  
  .location {
    display: flex;
    align-items: center;
    gap: 5px;
    font-size: 14px;
    opacity: 0.8;
    margin: 0;
  }
  
  .social-links {
    margin-top: 15px;
    display: flex;
    gap: 10px;
  }
  
  .social-link {
    display: inline-flex;
    align-items: center;
    gap: 5px;
    padding: 6px 12px;
    background-color: rgba(255, 255, 255, 0.2);
    border-radius: 20px;
    color: white;
    text-decoration: none;
    font-size: 14px;
    transition: all 0.2s ease;
  }
  
  .social-link:hover {
    background-color: rgba(255, 255, 255, 0.3);
    transform: translateY(-2px);
  }
  
  .social-link.linkedin {
    background-color: #0077b5;
  }
  
  .modal-content {
    padding: 30px;
  }
  
  .detail-section {
    margin-bottom: 25px;
  }
  
  .detail-section h3 {
    font-size: 18px;
    margin: 0 0 15px 0;
    color: var(--secondary);
    display: flex;
    align-items: center;
    gap: 8px;
  }
  
  .detail-card {
    background-color: #f8f9fa;
    border-radius: 8px;
    padding: 15px;
    margin-bottom: 15px;
    border-left: 4px solid var(--primary);
  }
  
  .detail-card h4 {
    margin: 0 0 8px 0;
    font-size: 16px;
    color: var(--secondary);
  }
  
  .date-range {
    font-size: 14px;
    color: #666;
    margin: 0 0 8px 0;
  }
  
  .job-location {
    display: flex;
    align-items: center;
    gap: 5px;
    font-size: 14px;
    color: #666;
    margin: 0 0 8px 0;
  }
  
  .description {
    font-size: 14px;
    line-height: 1.5;
    margin: 10px 0;
    color: #333;
  }
  
  .industry {
    font-size: 14px;
    color: #666;
    margin: 8px 0;
  }
  
  .company-link,
  .school-link {
    display: inline-flex;
    align-items: center;
    gap: 5px;
    font-size: 14px;
    color: var(--primary);
    text-decoration: none;
    margin-top: 8px;
  }
  
  .company-link:hover,
  .school-link:hover {
    text-decoration: underline;
  }
  
  .skills-container {
    display: flex;
    flex-wrap: wrap;
    gap: 8px;
  }
  
  .skill-tag {
    background-color: var(--primary-light);
    color: var(--primary);
    padding: 5px 10px;
    border-radius: 15px;
    font-size: 13px;
    font-weight: 500;
    transition: all 0.2s ease;
  }
  
  .skill-tag:hover {
    background-color: var(--primary);
    color: white;
    transform: translateY(-2px);
  }
  
  .followers {
    background-color: #f8f9fa;
    border-radius: 8px;
    padding: 15px;
  }
  
  .followers p {
    margin: 0;
    font-size: 16px;
  }
  
  .followers strong {
    color: var(--primary);
    font-size: 18px;
  }
  
  /* Animation for the modal */
  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }
  
  @keyframes slideUp {
    from {
      transform: translateY(30px);
      opacity: 0;
    }
    to {
      transform: translateY(0);
      opacity: 1;
    }
  }
  
  /* Responsive adjustments */
  @media (max-width: 768px) {
    .detail-modal {
      width: 95%;
      max-height: 85vh;
    }
  
    .profile-info {
      flex-direction: column;
      text-align: center;
    }
  
    .social-links {
      justify-content: center;
    }
  
    .modal-header {
      padding: 20px;
    }
  
    .modal-content {
      padding: 20px;
    }
  }
