// src/components/StartupCard.js
import React, { useState } from 'react';
import { Card, CardContent, Typography, CardActions, Button, Collapse, Link as MuiLink } from '@mui/material';
import { Link } from 'react-router-dom';

function StartupCard({ startup }) {
  const [expanded, setExpanded] = useState(false);

  const handleToggleDescription = () => {
    setExpanded((prev) => !prev);
  };

  return (
    <Card sx={{ marginBottom: '1rem', boxShadow: 3 }}>
      <CardContent>
        <Typography variant="h5" sx={{ fontWeight: 600 }}>
          {startup.startupName}
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" sx={{ mt: 0.5 }}>
          Founders: {startup.founderNames.join(', ')}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
          College: {startup.college}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Industry: {startup.companyIndustry}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Years of Experience: {startup.yearsExperience}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={handleToggleDescription}>
          {expanded ? 'Hide Details' : 'Show Details'}
        </Button>
        {startup.website && (
          <Button
            size="small"
            component={MuiLink}
            href={startup.website}
            target="_blank"
            rel="noopener"
          >
            Website
          </Button>
        )}
        {startup.linkedInPage && (
          <Button
            size="small"
            component={MuiLink}
            href={startup.linkedInPage}
            target="_blank"
            rel="noopener"
          >
            LinkedIn
          </Button>
        )}
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography variant="body1">{startup.description}</Typography>
        </CardContent>
      </Collapse>
    </Card>
  );
}

export default StartupCard;
